import React from 'react';
import { Link } from 'react-router-dom';
import './card.scss';

const Card = (props) => {
  return(
    <div>
    <ul className="row card-container">
    {
      props.cards.map((card, i) => {
        return <li className="card">
          <img className="card-img-top" src="http://lorempixel.com/400/200" alt="Card image cap"/>
          <div className="card-body">
            <h5 className="card-title">{card.id}</h5>
            <p className="card-text">{card.body}</p>
            <Link to={{ pathname: '/card', state: { cardId: card.id} }} className="btn btn-primary">View Details</Link>
          </div>
        </li>
      }

      )
    }
    </ul>
    </div>
  );
}

export default Card;
